mapper.select:
SELECT


,news.news_name as newsName
,news.news_uuid_number as newsUuidNumber
,news.news_photo as newsPhoto
,news.news_video as newsVideo
,news.zan_number as zanNumber
,news.cai_number as caiNumber
,news.news_types as newsTypes
,news.news_clicknum as newsClicknum
,news.fabu_time as fabuTime
,news.news_content as newsContent
,news.shangxia_types as shangxiaTypes
,news.news_delete as newsDelete
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail

FROM news_liuyan  a
left JOIN news news ON a.news_id = news.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



        and a.id in

        #{item}



        and (
            a.news_id = #{params.newsId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


        and a.news_liuyan_text like CONCAT('%',#{params.newsLiuyanText},'%')


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.reply_text like CONCAT('%',#{params.replyText},'%')


          and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


          and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 




        and a.news_id IS NOT NULL


        and news.news_name like CONCAT('%',#{params.newsName},'%')


        and news.news_uuid_number like CONCAT('%',#{params.newsUuidNumber},'%')


        and news.news_video like CONCAT('%',#{params.newsVideo},'%')


          and news.zan_number >= #{params.zanNumberStart}   


          and news.zan_number <= #{params.zanNumberEnd}   


        and news.zan_number = #{params.zanNumber}


          and news.cai_number >= #{params.caiNumberStart}   


          and news.cai_number <= #{params.caiNumberEnd}   


        and news.cai_number = #{params.caiNumber}


        and news.news_types = #{params.newsTypes}



          and news.news_clicknum >= #{params.newsClicknumStart}   


          and news.news_clicknum <= #{params.newsClicknumEnd}   


        and news.news_clicknum = #{params.newsClicknum}


          and UNIX_TIMESTAMP(news.fabu_time) >= UNIX_TIMESTAMP(#{params.fabuTimeStart}) 


          and UNIX_TIMESTAMP(news.fabu_time) <= UNIX_TIMESTAMP(#{params.fabuTimeEnd}) 


        and news.news_content like CONCAT('%',#{params.newsContent},'%')


        and news.shangxia_types = #{params.shangxiaTypes}



          and news.news_delete >= #{params.newsDeleteStart}   


          and news.news_delete <= #{params.newsDeleteEnd}   


        and news.news_delete = #{params.newsDelete}


          and UNIX_TIMESTAMP(news.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(news.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


order by a.${params.sort} ${params.order}
